class SlideShow{constructor(e){this.slideNumber=1,this.title=e,this.slides=document.querySelectorAll(`[data-slide="${e}"]`),this.slideNextBtn=document.querySelector(`[data-prev-btn=${e}]`),this.slidePrevBtn=document.querySelector(`[data-next-btn=${e}]`),this.show(this.slideNumber),this.addListeners()}addListeners(){this.slidePrevBtn.addEventListener("click",(()=>{this.change(1)})),this.slideNextBtn.addEventListener("click",(()=>{this.change(-1)}))}show(e){e>this.slides.length&&(this.slideNumber=1),e<1&&(this.slideNumber=this.slides.length);for(let e=0;e<this.slides.length;e++)this.slides[e].style.display="none";this.slides[this.slideNumber-1].style.display="block"}change(e){this.show(this.slideNumber+=e)}}slidesKorean=new SlideShow("korean"),slidesAnswer=new SlideShow("answer");