let htmlColorSchemeBtn,currentScheme=localStorage.scheme?localStorage.scheme:"light";function setScheme(e){let t=["light","dark"].includes(e)?e:"light";localStorage.scheme=t,currentScheme=t,document.documentElement.dataset.scheme=t}function switchScheme(){setScheme("dark"===currentScheme?"light":"dark")}setScheme(currentScheme),document.addEventListener("DOMContentLoaded",(function(){htmlColorSchemeBtn=document.getElementById("js-color-scheme-btn"),htmlColorSchemeBtn.addEventListener("click",(()=>{switchScheme()}))}));